# pywin32_system32 module
# This module provides the path to pywin32 system DLLs
import os
import sys

# Set __path__ to point to the Python directory where DLLs might be located
# For embedded Python, DLLs are typically in the Python root directory or in site-packages/win32
python_dir = os.path.dirname(sys.executable)
win32_dir = os.path.join(os.path.dirname(__file__), '..', 'win32')
win32_dir = os.path.abspath(win32_dir)

# Try multiple possible locations
possible_paths = [python_dir, win32_dir]
__path__ = [p for p in possible_paths if os.path.isdir(p)]

